import uniqid from 'uniqid'
import { useState } from 'react';
const genderOption = ['male','female'];
const majorOption = ['ICT','EcE','AME',"PRE"];
const acadamicOption = ['firstYear','secondYear','thirdYear','fourthYear','fifthYear','finalYear'];
const Form = () => {
    const [name,setName] = useState("");
    const [nrc,netNrc] = useState("");
    const [gender,setGender] = useState("");
    const [address,setAddress] = useState("");
    const [major,setMajor] = useState("");
    const [acadamicYear,setAcadamicYear] = useState("");
    const [attendanceYear,setAttendanceYear] = useState("");

    const handleSubmit = (e) =>{
        e.preventDefault();
        console.log(e.target);
    }
    return ( 
        <form onSubmit={handleSubmit}> 
            <label>Name :</label><br></br>
            <input type="text" name="name" onChange={(e)=>setName(e.target.value)}/><br></br>
            <label>NRC :</label><br></br>
            <input type="text" name="nrc" onChange={(e)=>setNrc(e.target.value)}/><br></br>
            <label>Gender</label>
            <br></br>
            {genderOption.map((gender)=>(
            <li key={uniqid}> 
            <input type="checkbox" setGender={(e)=>setGender(e.target.value)}/><label>{gender}</label><br></br>
            </li>))}

            <label>Address</label>
            <br></br>
            <textarea name="address">

            </textarea>
            <br></br>
            <label>Major</label><br></br>
            {majorOption.map((major)=>(
            <li key={uniqid()}>
            <input type="radio"/><label>{major}</label><br></br>
            </li>))}
            <label>Academic Year</label>
            <select name="acadamicYear">
                {acadamicOption.map((acadamic)=>(
                    <option>{acadamic}</option>
                ))}
            </select>
            <br></br>
            <label>attendanceYear</label>
            <select name="attendanceYear">
            {acadamicOption.map((acadamic)=>(
                    <option>{acadamic}</option>
                ))}
            </select>
            <br></br>
            <button type='submit'>submit</button>
        </form>
     );
}

export default Form;
