import uniqid from 'uniqid'
import { useState } from 'react';
const genderOption = ['male','female'];
const majorOption = ['ICT','EcE','AME',"PRE"];
const acadamicOption = ['firstYear','secondYear','thirdYear','fourthYear','fifthYear','finalYear'];
const Form = () => {
    const [name,setName] = useState("");
    const [nrc,setNrc] = useState("");
    const [gender,setGender] = useState("");
    const [address,setAddress] = useState("");
    const [major,setMajor] = useState("");
    const [acadamicYear,setAcadamicYear] = useState("");
    const [attendanceYear,setAttendanceYear] = useState("");
    const [formData,setFormData] = useState({});

    const handleSubmit = (e) =>{
        e.preventDefault();
       //const {name,value} = e.target;
       //setFormData({...formData,[name]: value});
       //console.log(formData)
        console.log(e.target.value)
    }
    return ( 
        <form onSubmit={handleSubmit}> 
            <label>Name :</label><br></br>
            <input type="text" name="name" value={name} onChange={setFormData}/><br></br>
            <label>NRC :</label><br></br>
            <input type="text" name="nrc" value={nrc} onChange={setFormData}/><br></br>
            <label>Gender</label>
            <br></br>
            {genderOption.map((gender)=>(
            <li key={uniqid()}> 
            <input type="checkbox" onChange={(e)=>setGender(e.target.checked)}/><label>{gender}</label><br></br>
            </li>))}

            <label>Address</label>
            <br></br>
            <textarea name="address" onChange={setFormData}>

            </textarea>
            <br></br>
            <label>Major</label><br></br>
            {majorOption.map((major)=>(
            <li key={uniqid()}>
            <input type="radio" onChange={setFormData} value={major}/><label>{major}</label><br></br>
            </li>))}
            <label>Academic Year</label>
            <select name="acadamicYear" onChange={setFormData}>
                {acadamicOption.map((acadamic)=>(
                    <option key={uniqid()}>{acadamic}</option>
                ))}
            </select>
            <br></br>
            <label>attendanceYear</label>
            <select name="attendanceYear" onChange={setFormData}>
            {acadamicOption.map((acadamic)=>(
                    <option key={uniqid()}>{acadamic}</option>
                ))}
            </select>
            <br></br>
            <button type='submit'>submit</button>
        </form>
     );
}

export default Form;
