import uniqid from 'uniqid'
import { useState } from 'react';
const genderOption = ['male','female'];
const major = ['ICT','EcE','AME',"PRE"];
const acadamicOption = ['firstYear','secondYear','thirdYear','fourthYear','fifthYear','finalYear'];
const Form = () => {
    const [name,setName] = useState("");
    const [nrc,netNrc] = useState("");
    const [gender,setGender] = useState("");
    const [address,setAddress] = useState("");
    const [major,setMajor] = useState("");
    const [acadamicYear,setAcadamicYear] = useState("");
    const [attendanceYear,setAttendanceYear] = useState("");

    const handleSubmit = (e) =>{
        e.preventDefault();
        console.log(e.target);
    }
    return ( 
        <form onSubmit={handleSubmit}> 
            <label>Name :</label>
            <input type="text" name="name"/><br></br>
            <label>NRC :</label>
            <input type="text" name="nrc"/><br></br>
            <label>Gender</label>
            <br></br>
            {genderOption.map((gender)=>(
            <li>
            <input type="checkbox"/><label>Male</label><br></br>
            </li>))}

            <label>Address</label>
            <br></br>
            <textarea name="address">

            </textarea>
            <br></br>
            <label>Major</label><br></br>
            <input type="radio"/><label>ICT</label><br></br>
            <input type="radio"/><label>EcE</label><br></br>
            <input type="radio"/><label>AME</label><br></br>
            <input type="radio"/><label>PRE</label><br></br>
            <label>Academic Year</label>
            <select name="acadamicYear">
                <option value="firstYear">First Year</option>
                <option value="secondYear">Second Year</option>
                <option value="thirdYear">Third Year</option>
                <option value="fourthYear">Fourth Year</option>
                <option value="fifthYear">Fifth Year</option>
                <option value="finalYear">Final Year</option>
            </select>
            <br></br>
            <label>attendanceYear</label>
            <select name="attendanceYear">
                <option value="firstYear">First Year</option>
                <option value="secondYear">Second Year</option>
                <option value="thirdYear">Third Year</option>
                <option value="fourthYear">Fourth Year</option>
                <option value="fifthYear">Fifth Year</option>
                <option value="finalYear">Final Year</option>
            </select>
            <br></br>
            <button type='submit'>submit</button>
        </form>
     );
}

export default Form;
